import torch
from trl import DPOTrainer, DPOConfig
from transformers import AutoTokenizer, AutoModelForCausalLM, TrainingArguments
from peft import PeftModel, LoraConfig, get_peft_model, TaskType
from datasets import load_dataset
import os

# Disable WANDB logging if not needed
os.environ["WANDB_DISABLED"] = "true"

# Define base model and instruction checkpoint
base_model_name = "TinyLlama/TinyLlama-1.1B-intermediate-step-1431k-3T"
instruction_checkpoint = '/content/tinyllama-instruction/checkpoint-3'

# Load tokenizer
tokenizer = AutoTokenizer.from_pretrained(base_model_name)
if tokenizer.pad_token is None:
    tokenizer.pad_token = tokenizer.eos_token

# Load dataset for DPO
dataset = load_dataset("csv", data_files="/content/pharma_preference_data.csv")["train"]

# --- Prepare Policy Model (instruction-tuned + new DPO LoRA) ---
# 1. Load the base model
policy_model_base = AutoModelForCausalLM.from_pretrained(
    base_model_name,
    torch_dtype=torch.float16, # Use float16 for memory efficiency
    device_map="auto"
)

# 2. Load the instruction-tuned PEFT adapters on top of the base model
policy_model = PeftModel.from_pretrained(policy_model_base, instruction_checkpoint)

# 3. Define LoRA config for DPO training
lora_config_dpo = LoraConfig(
    task_type=TaskType.CAUSAL_LM,
    r=8,
    lora_alpha=16,
    lora_dropout=0.05,
    target_modules=["q_proj", "v_proj"],
    bias="none"
)

# 4. Apply DPO-specific LoRA adapters on top of the instruction-tuned model
# This creates the trainable policy model for DPO
policy_model_with_dpo_lora = get_peft_model(policy_model, lora_config_dpo)


# --- Prepare Reference Model (frozen instruction-tuned model) ---
# 1. Load another instance of the base model
ref_model_base = AutoModelForCausalLM.from_pretrained(
    base_model_name,
    torch_dtype=torch.float16, # Use float16 for memory efficiency
    device_map="auto"
)

# 2. Load the instruction-tuned PEFT adapters onto this separate base model
ref_model = PeftModel.from_pretrained(ref_model_base, instruction_checkpoint)

# 3. Merge and unload the adapters to get a full instruction-tuned model as the reference
# This ensures the reference model is a complete instruction-tuned model and not an adapter layer on its own
ref_model = ref_model.merge_and_unload()

# Ensure the reference model is moved to the same device as the policy model's base, if not already.
# DPOTrainer will handle freezing its parameters.
# This line ensures both models are on the same device, which was the source of the RuntimeError.
ref_model = ref_model.to(policy_model_with_dpo_lora.device)


# DPO training arguments
dpo_args = DPOConfig(
    output_dir="./tinyllama-preference-alignment",
    learning_rate=2e-5,
    per_device_train_batch_size=1,
    gradient_accumulation_steps=8,
    num_train_epochs=1,
    beta=0.1,
    report_to=[],
    logging_dir=None,
    loss_type="sigmoid",
    remove_unused_columns=False
)

# Initialize DPOTrainer with explicit policy and reference models
trainer = DPOTrainer(
    model=policy_model_with_dpo_lora,
    ref_model=ref_model, # Explicitly pass the prepared reference model
    args=dpo_args,
    train_dataset=dataset,
    tokenizer=tokenizer, # Pass tokenizer here
)

# Train the model
trainer.train()

# Save & test the model
trainer.save_model("/content/tinyllama-preference-alignment")
tokenizer.save_pretrained("/content/tinyllama-preference-alignment")

# Test generation (optional, can be done in a separate cell)
trained_dpo_model = AutoModelForCausalLM.from_pretrained("/content/tinyllama-preference-alignment", device_map="auto")
prompt = "Explain the mechanism of action of Metformin in simple terms."
inputs = tokenizer(prompt, return_tensors="pt").to("cuda")

outputs = trained_dpo_model.generate(
    **inputs,
    max_new_tokens=100,
    temperature=0.8,
    top_p=0.9,
    do_sample=True,
    repetition_penalty=1.1
)

print("\nModel Output after DPO:\n")
print(tokenizer.decode(outputs[0], skip_special_tokens=True))